const keys=document.querySelectorAll(".key");function keysOn(e){e.addEventListener("mouseover",keyOver,!1),e.addEventListener("mouseout",keyOut,!1)}function keyOver(e){const t=e.target,n=t.getAttribute("data-key-id"),a=document.querySelector(`audio[data-key-id='${n}']`);t.classList.add("playing"),a.currentTime=0,a.play()}function keyOut(e){e.target.classList.remove("playing")}keys.forEach(keysOn);